in vec2 uv0;
uniform sampler2D cc_spriteTexture;
float calcu(vec2 uv, float width) {
  float a = 0.0;
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(0.0, 0.0)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(0.0, width)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(0.0, -width)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(width, 0.0)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(width, width)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(width, -width)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(-width, 0.0)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(-width, width)).a);
  a = max(a, texture(cc_spriteTexture, uv + cc_nativeSize.zw * vec2(-width, -width)).a);
  return a;
}
vec4 frag () {
  float a = calcu(uv0, border.a);
  vec4 origin = texture(cc_spriteTexture, uv0);
  vec4 o = vec4(border.rgb, a);
  float t = origin.a + a + 0.0001;
  float fo = origin.a / t;
  float fa = a / t;
  o.rgb = origin.rgb * fo + o.rgb * fa;
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

